client_script:
console.log("Hello World");

document.getElementsByClassName("nav-avatar")[0].style.display = "none";
document.getElementsByClassName("navbar-brand")[0].style.display = "none";


window.onload = function() {

    document.getElementsByClassName("nav-avatar")[0].style.display = "none";
    document.getElementsByClassName("navbar-brand")[0].style.display = "none";
};

let value = localStorage.getItem('email');
const emailField = document.querySelector('input[data-fieldname="email"]');
emailField.value=value;
let email = value;
emailField.setAttribute("readonly", true);


 frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'Gym Class Booking',
            name: email  
        },
        callback: function(res) {
            let booking = res.message;
            console.log("Gym Class Booking response:", booking);

            if (booking && booking.trainer_name) {
                let trainer_name = booking.trainer_name;
                console.log("Trainer name from booking:", trainer_name);
                setTrainerFieldOptions([trainer_name], trainer_name);
            } else {
                setTrainerFieldOptions([], '');
            }
        }
    });

function setTrainerFieldOptions(options, value_to_set) {

    frappe.web_form.set_df_property('trainer_name', 'options', options);


    let field = frappe.web_form.get_field('trainer_name');
    if (field && field.df) {
        field.df.options = options;
    }


    frappe.web_form.set_value('trainer_name', value_to_set);


    if (field && field.refresh) {
        field.refresh();
    }
}
